SELECT flights.id, airlines.icao, flights.flightnumber, airports.icao, flights.fromAirport, flights.toAirport, flights.aircraft, flights.pic, flights.firstofficer, flights.secondofficer, flights.preparedby, flights.atcroute, flights.releasefuel FROM flights INNER JOIN airlines ON flights.airline = airlines.id LEFT JOIN airports ON flights.fromAirport = airports.id AND flights.toAirport = airports.id